              startAdornment: (
                <InputAdornment position="start">
                  <SearchIcon sx={{ 
                    fontSize: 24, 
                    color: alpha('#000', 0.4),
                    animation: isSearchExpanded ? 'pulse 1s cubic-bezier(0.16, 1, 0.3, 1)' : 'none',
                    '@keyframes pulse': {
                      '0%': {
                        transform: 'scale(0.8)',
                        opacity: 0.5,
                      },
                      '50%': {
                        transform: 'scale(1.1)',
                      },
                      '100%': {
                        transform: 'scale(1)',
                        opacity: 1,
                      }
                    }
                  }} />
                </InputAdornment>
              ),
            }}
          />

          {/* Search Results */}
          <Box sx={{ 
            mt: 3, 
            overflow: 'auto',
            maxHeight: '50vh',
            '&::-webkit-scrollbar': { width: 4 },
            '&::-webkit-scrollbar-track': { backgroundColor: 'transparent' },
            '&::-webkit-scrollbar-thumb': {
              backgroundColor: alpha('#000', 0.08),
              borderRadius: 2,
              '&:hover': { backgroundColor: alpha('#000', 0.15) }
            },
            animation: isSearchExpanded ? 'fadeIn 0.7s cubic-bezier(0.16, 1, 0.3, 1)' : 'none',
            '@keyframes fadeIn': {
              '0%': {
                opacity: 0,
              },
              '100%': {
                opacity: 1,
              }
            }
          }}>
            {searchQuery.trim() ? (
              searchResults.length > 0 ? (
                <List>
                  {searchResults.map((result: TreeNode, index: number) => (
                    <ListItem
                      key={index}
                      onClick={() => navigateToSearchResult(result)}
                      sx={{
                        cursor: 'pointer',
                        py: 1.5,
                        px: 2,
                        transition: 'all 0.2s ease-in-out',
                        borderRadius: '8px',
                        '&:hover': {
                          backgroundColor: alpha(theme.palette.primary.main, 0.04),
                          transform: 'translateX(2px)'
                        },
                      }}
                    >
                      <ListItemIcon sx={{ 
                        minWidth: 32,
                        color: alpha('#000', 0.5),
                      }}>
                        {result.type === 'folder' ? (
                          <FolderIcon sx={{ fontSize: 20 }} />
                        ) : (
                          <InsertDriveFileIcon sx={{ fontSize: 20 }} />
                        )}
                      </ListItemIcon>
                      <Box sx={{ flex: 1 }}>
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 0.5 }}>
                          <Typography
                            sx={{
                              fontSize: '0.875rem',
                              fontWeight: 600,
                              color: alpha('#000', 0.87)
                            }}
                          >
                            {result.name}
                          </Typography>
                          {result.dataOwner && (
                            <Chip
                              label={`Owner: ${result.dataOwner}`}
                              size="small"
                              sx={{
                                height: 20,
                                fontSize: '0.625rem',
                                fontWeight: 500,
                                backgroundColor: alpha('#9C27B0', 0.1),
                                color: '#9C27B0',
                              }}
                            />
                          )}
                          {result.dataSource && (
                            <Chip
                              label={`Source: ${result.dataSource}`}
                              size="small"
                              sx={{
                                height: 20,
                                fontSize: '0.625rem',
                                fontWeight: 500,
                                backgroundColor: alpha('#2196F3', 0.1),
                                color: '#2196F3',
                              }}
                            />
                          )}
                          {result.latency && (
                            <Chip
                              label={`Latency: ${result.latency}`}
                              size="small"
                              sx={{
                                height: 20,
                                fontSize: '0.625rem',
                                fontWeight: 500,
                                backgroundColor: alpha('#4CAF50', 0.1),
                                color: '#4CAF50',
                              }}
                            />
                          )}
                        </Box>
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                          <Typography
                            sx={{
                              fontSize: '0.75rem',
                              color: alpha('#000', 0.5),
                              display: 'flex',
                              alignItems: 'center',
                              gap: 0.5
                            }}
                          >
                            <FolderOpenIcon sx={{ fontSize: 16 }} />
                            {getPathToNode(result, transformToTree(transformUserData(userData))).join(' > ')}
                          </Typography>
                        </Box>
                      </Box>
                    </ListItem>
                  ))}
                </List>
              ) : (
                <Box sx={{ 
                  p: 3, 
                  textAlign: 'center',
                  color: alpha('#000', 0.5)
                }}>
                  <Typography variant="body1">
                    No results found for "{searchQuery}"
                  </Typography>
                </Box>
              )
            ) : (
              <Box sx={{ 
                p: 3, 
                textAlign: 'center',
                color: alpha('#000', 0.5)
              }}>
                <Typography variant="body1">
                  Start typing to search...
                </Typography>
              </Box>
            )}
          </Box>
        </Box>
      </Box>

      {/* Add a style to blur the side navigation when search is expanded */}
      <style>
        {`
          body.search-overlay-active nav,
          body.search-overlay-active aside {
            filter: blur(8px);
            transition: filter 0.3s ease;
          }
        `}
      </style>

      <Box sx={{ 
        p: '16px 24px', 
        flexShrink: 0,
        backgroundColor: 'white',
        borderBottom: '1px solid',
        borderColor: alpha(theme.palette.divider, 0.08),
        position: 'sticky',
        top: 0,
        zIndex: 10,
      }}>
        <Box sx={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center',
          width: '100%'
        }}>
          <ToggleButtonGroup
            value={tabValue}
            exclusive
            onChange={handleNavigationChange}
            aria-label="navigation type"
            sx={{
              justifyContent: 'flex-start',
              '& .MuiToggleButton-root': {
                textTransform: 'none',
                px: 2,
                py: 1,
                border: 'none',
                borderRadius: '8px',
                mx: 0,
                mr: 1,
                '&.Mui-selected': {
                  backgroundColor: alpha(theme.palette.primary.main, 0.08),
                  color: theme.palette.primary.main,
                  '&:hover': {
                    backgroundColor: alpha(theme.palette.primary.main, 0.12),
                  }
                },
                '&:hover': {
                  backgroundColor: alpha('#000', 0.04),
                }
              }
            }}
          >
            {navigationOptions.map((option, index) => (
              <ToggleButton key={index} value={index} aria-label={option.label}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  {option.icon}
                  <Typography sx={{ fontSize: '0.875rem', fontWeight: 500 }}>
                    {option.label}
                  </Typography>
                </Box>
              </ToggleButton>
            ))}
          </ToggleButtonGroup>

          <TextField
            placeholder="Search..."
            value={searchQuery}
            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setSearchQuery(e.target.value)}
            onClick={() => setIsSearchExpanded(true)}
            size="small"
            sx={{
              width: '500px',
              '& .MuiOutlinedInput-root': {
                borderRadius: '8px',
                backgroundColor: alpha('#fff', 0.8),
                '& fieldset': {
                  borderColor: alpha(theme.palette.divider, 0.1),
                },
                '&:hover fieldset': {
                  borderColor: alpha(theme.palette.primary.main, 0.3),
                },
                '&.Mui-focused fieldset': {
                  borderColor: alpha(theme.palette.primary.main, 0.5),
                }
              }
            }}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <SearchIcon sx={{ fontSize: 18, color: alpha('#000', 0.4) }} />
                </InputAdornment>
              ),
            }}
          />
        </Box>
      </Box>

      {/* Content Area */}
      <Box sx={{ 
        flex: 1, 
        minHeight: 0, 
        display: 'flex', 
        flexDirection: 'column',
        position: 'relative',
        p: '16px 24px',
        gap: 2
      }}>
        <Box sx={{ display: 'flex', gap: 2, flex: 1, minHeight: 0 }}>
          {/* Navigation Columns */}
          <Box sx={{ 
            flex: 1,
            backgroundColor: 'white',
            overflow: 'hidden',
            display: 'flex',
            borderRadius: '12px',
            boxShadow: `0 2px 12px ${alpha('#000', 0.04)}`,
          }}>
            <Box 
              ref={scrollContainerRef}
              sx={{ 
                flex: 1,
                display: 'flex',
                overflowX: 'auto',
                overflowY: 'hidden',
                scrollBehavior: 'smooth',
                '&::-webkit-scrollbar': { height: 4 },
                '&::-webkit-scrollbar-track': { backgroundColor: 'transparent' },
                '&::-webkit-scrollbar-thumb': { 
                  backgroundColor: alpha('#000', 0.08),
                  borderRadius: 2,
                  '&:hover': { backgroundColor: alpha('#000', 0.15) }
                }
              }}
            >
              {columns.map((column: ColumnData, index: number) => renderColumn(column, index))}
              {columns.length === 1 && (
                <Box
                  sx={{
                    width: '100%',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    backgroundColor: alpha(theme.palette.background.paper, 0.5),
                    color: alpha('#000', 0.4),
                    flexDirection: 'column',
                    gap: 1.5,
                    p: 4
                  }}
                >
                  <FolderIcon sx={{ fontSize: 40, color: alpha('#000', 0.12) }} />
                  <Typography variant="h6" sx={{ 
                    color: alpha('#000', 0.6),
                    fontWeight: 500,
                  }}>
                    No Items Selected
                  </Typography>
                  <Typography variant="body2" align="center" sx={{ 
                    maxWidth: 300,
                    color: alpha('#000', 0.4),
                    fontSize: '0.875rem',
                    lineHeight: 1.5
                  }}>
                    Select a folder to explore its contents and view detailed information
                  </Typography>
                </Box>
              )}
            </Box>
          </Box>

          {/* Details Panel */}
          {renderDetailsPanel()}
        </Box>
      </Box>
    </Box>
  );
};

export default DataWizard;
